package com.operadores;

public class OperadoresB {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 50, num2 = 70,num3 = 80;
		int quadNum1 = num1 * num1;
		int quadNum2 = num2 * num2;
		int quadNum3 = num3 * num3;
		
		int soma = quadNum1 + quadNum2 + quadNum3;
		System.out.println(quadNum1);
		
		System.out.println("A soma de todos os valores é: " + soma);
		
	}

}
